BEGIN
	SET NOCOUNT ON;

	INSERT INTO dbo.[Genres]([Name])
	VALUES (@Name);

	SET @Id = SCOPE_IDENTITY();
END

CREATE PROCEDURE [dbo].[spGenres_Create]
	@Name NVARCHAR(50),
	@Id INT OUTPUT
AS

BEGIN SET NOCOUNT ON;
DECLARE @ERROR INT SET TRANSACTION ISOLATION LEVEL REPEATABLE READ BEGIN TRANSACTION
DELETE FROM dbo.[MovieGenres] WHERE GenreId = @Id;
SELECT @ERROR = @@ERROR IF (@ERROR <> 0) BEGIN ROLLBACK TRANSACTION END
DELETE FROM dbo.[Genres] WHERE Id = @Id
SELECT @ERROR = @@ERROR IF (@ERROR <> 0) BEGIN ROLLBACK TRANSACTION END ELSE BEGIN COMMIT TRANSACTION END END

CREATE PROCEDURE [dbo].[spGenres_Delete]
	@Id INT
AS

BEGIN	
	SET NOCOUNT ON;

	SELECT [Id], [Name]
	FROM dbo.[Genres]
	WHERE Id = @Id;
END

CREATE PROCEDURE [dbo].[spGenres_GetById]
	@Id INT
AS

BEGIN
	SET NOCOUNT ON;

	UPDATE dbo.[Genres] SET
		Name = @Name
	WHERE Id = @Id;
END

CREATE PROCEDURE [dbo].[spGenres_Update]
	@Id INT,
	@Name NVARCHAR(50)
AS

BEGIN
	SET NOCOUNT ON;

	INSERT INTO dbo.[MovieActors](PersonId, MovieId, [Character], [Order])
