	@Title NVARCHAR(50),
	@Summary NVARCHAR(MAX),
	@InTheaters BIT,
	@Trailer NVARCHAR(MAX),
	@ReleaseDate DATETIME2,
	@Poster NVARCHAR(MAX)
AS

BEGIN
	SET NOCOUNT ON;

	UPDATE dbo.[People] SET
		RoleId = (SELECT Id FROM Roles WHERE Name = @RoleName)
	WHERE Id = @Id;
END

CREATE PROCEDURE [dbo].[spRoles_Create]
	@Id INT,
	@RoleName NVARCHAR(50)
AS

BEGIN
	SET NOCOUNT ON;

	UPDATE dbo.[People] SET
		RoleId = NULL
	WHERE Id = @Id;
END

CREATE PROCEDURE [dbo].[spRoles_Delete]
	@Id INT,
	@RoleName NVARCHAR(50)
AS

BEGIN
	SET NOCOUNT ON;

	INSERT INTO dbo.[People](RoleId, Email, [Password], [Name], Biography, Picture, DateOfBirth)
	VALUES (@RoleId, @Email, @Password, @Name, @Biography, @Picture, @DateOfBirth);

	SET @Id = SCOPE_IDENTITY();
END

CREATE PROCEDURE [dbo].[spUsers_Create]
	@RoleId INT,
	@Email NVARCHAR(250),
	@Password NVARCHAR(100),
	@Name NVARCHAR(50),
	@Biography NVARCHAR(MAX),
	@Picture NVARCHAR(250),
	@DateOfBirth DATETIME2,
	@Id INT OUTPUT
AS

BEGIN
	SET NOCOUNT ON;

	DELETE FROM dbo.[People]
	WHERE Id = @Id;
END

CREATE PROCEDURE [dbo].[spUsers_Delete]
	@Id INT
AS

BEGIN SET NOCOUNT ON;
SELECT p.Id, RoleId, Email, [Password], p.[Name], Biography, Picture, DateOfBirth, r.[Name] AS RoleName FROM dbo.People p JOIN dbo.Roles r ON p.RoleId = r.Id WHERE p.Id = @Id
SELECT [Id], [PersonId], [MovieId], [Character], [Order] FROM dbo.MovieActors WHERE PersonId = @Id END

